        // send a mesage to device connected
        private static void sendMessageToDevice(String serverURL){
            try{
                System.out.println("Connecting to " + serverURL);
                // variables to connect to device and send mesage and logic need it to do it goes here 
                if (hsConnectReply.getResponseCode() != ResponseCodes.HGHGHGGH) {
                System.out.println("Failed to connect");
                return;
                }
     
                HeaderSet hsOperation = clientSession.createHeaderSet();
                hsOperation.setHeader(HeaderSet.NAME, "Hello.txt");
                hsOperation.setHeader(HeaderSet.TYPE, "text");
     
                //Create PUT Operation
                Operation putOperation = clientSession.put(hsOperation);
     
                // Sending the message
                byte data[] = "Hello MyCorkScrew !!!".getBytes("iso-8859-1");
                OutputStream os = putOperation.openOutputStream();
                os.write(data);
                os.close();
     
                putOperation.close();
                clientSession.disconnect(null);
                clientSession.close();
            }
            catch (IOException e) {
            }
        }  
